// handleHealthRequest handles health status requests.
// Called from HandlePredict when ?health=true is passed.
func handleHealthRequest(w http.ResponseWriter, r *http.Request, app *Application) {
	ctx := r.Context()

	// Get current state from any existing aggregate
	// Default to initial inventory values
	currentState := map[string]int{
		"coffee_beans":    int(GetCurrentInventory()["coffee_beans"]),
		"milk":            int(GetCurrentInventory()["milk"]),
		"cups":            int(GetCurrentInventory()["cups"]),
		"orders_pending":  0,
		"orders_complete": 0,
	}

	// Try to get actual state from an aggregate if we have an ID
	aggregateID := r.URL.Query().Get("id")
	if aggregateID != "" {
		agg, err := app.GetState(ctx, aggregateID)
		if err == nil && agg != nil {
			if state, ok := agg.State().(map[string]int); ok {
				for k, v := range state {
					currentState[k] = v
				}
			}
		}
	}

	// Update health tracker with current state
	UpdateHealthFromState(currentState)

	// Get health and metrics
	health, metrics := GetCurrentHealth()
	info := HealthStates[health]

	response := HealthResponse{
		Health:    health,
		Info:      info,
		Metrics:   metrics,
		State:     currentState,
		Timestamp: HealthTimestamp(),
	}

	api.JSON(w, http.StatusOK, response)
}

// HandlePredict runs ODE simulation and returns predicted resource levels.
// Also handles /api/coffeeshop/predict?health=true for health status.
func HandlePredict(app *Application) http.HandlerFunc {
	return func(w http.ResponseWriter, r *http.Request) {
		// Check if this is a health status request
		if r.URL.Query().Get("health") == "true" {
			handleHealthRequest(w, r, app)
			return
		}

		hoursStr := r.URL.Query().Get("hours")
		hours := PredictionConfig.TimeHours
		if hoursStr != "" {
			if h, err := strconv.ParseFloat(hoursStr, 64); err == nil && h > 0 {
				hours = h
			}
		}

		// Get current token counts
		currentTokens := GetCurrentInventory()

		result, err := RunSimulation(currentTokens, hours)
		if err != nil {
			api.Error(w, http.StatusInternalServerError, "SIMULATION_FAILED", err.Error())
			return
		}

		api.JSON(w, http.StatusOK, result)
	}
}

// HandleRunout returns predicted runout times for resources.
func HandleRunout(app *Application) http.HandlerFunc {
	return func(w http.ResponseWriter, r *http.Request) {
		currentTokens := GetCurrentInventory()

		result, err := RunSimulation(currentTokens, PredictionConfig.TimeHours)
		if err != nil {
			api.Error(w, http.StatusInternalServerError, "SIMULATION_FAILED", err.Error())
			return
		}

		api.JSON(w, http.StatusOK, result.RunoutTime)
	}
}

// HandleHealthEndpoint returns the current system health state and metrics.
// Mirrors go-pflow/examples/coffeeshop health classification.
// Note: Due to router limitations, use /api/coffeeshop/predict?health=true instead.
func HandleHealthEndpoint(app *Application) http.HandlerFunc {
	return func(w http.ResponseWriter, r *http.Request) {
		handleHealthRequest(w, r, app)
	}
}

// HandleNavigation returns the navigation menu, filtered by user roles.
func HandleNavigation(nav *Navigation) http.HandlerFunc {
	return func(w http.ResponseWriter, r *http.Request) {
		// Debug: check if this code is being executed
		w.Header().Set("X-Debug-Navigation", "modified-v1")
		userRoles := getNavUserRoles(r)

		items := []NavigationItem{}
		for _, item := range nav.Items {
			if len(item.Roles) == 0 {
				items = append(items, item)
				continue
			}

			if navHasAnyRole(userRoles, item.Roles) {
				items = append(items, item)
			}
		}

		api.JSON(w, http.StatusOK, map[string]interface{}{
			"brand": nav.Brand,
			"items": items,
		})
	}
}

// getNavUserRoles extracts user roles from the request context.
func getNavUserRoles(r *http.Request) []string {
	// No role-based access control configured
	_ = r // unused
	return nil
}

// navHasAnyRole checks if user has at least one of the required roles.
func navHasAnyRole(userRoles []string, requiredRoles []string) bool {
	for _, required := range requiredRoles {
		for _, userRole := range userRoles {
			if userRole == required {
				return true
			}
		}
	}
	return false
}





// HandleGetEvents returns the event history for an aggregate.
func HandleGetEvents(app *Application) http.HandlerFunc {
	return func(w http.ResponseWriter, r *http.Request) {
		ctx := r.Context()
		id := r.PathValue("id")
		from := getIntQueryParam(r, "from", 0)

		events, err := app.store.Read(ctx, id, from)
		if err != nil {
			api.Error(w, http.StatusInternalServerError, "READ_FAILED", err.Error())
			return
		}

		api.JSON(w, http.StatusOK, map[string]interface{}{
			"events": events,
		})
	}
}

// HandleGetStateAtVersion returns the aggregate state at a specific version.
func HandleGetStateAtVersion(app *Application) http.HandlerFunc {
	return func(w http.ResponseWriter, r *http.Request) {
		ctx := r.Context()
		id := r.PathValue("id")
		versionStr := r.PathValue("version")

		version := getInt(versionStr, 0)
		if version <= 0 {
			api.Error(w, http.StatusBadRequest, "INVALID_VERSION", "version must be a positive integer")
			return
		}

		events, err := app.store.Read(ctx, id, 0)
		if err != nil {
			api.Error(w, http.StatusInternalServerError, "READ_FAILED", err.Error())
			return
		}

		// Create temporary aggregate and replay up to version
		agg := NewAggregate(id)
		for _, evt := range events {
			if evt.Version > version {
				break
			}
			if err := agg.Apply(evt); err != nil {
				api.Error(w, http.StatusInternalServerError, "APPLY_FAILED", err.Error())
				return
			}
		}

		api.JSON(w, http.StatusOK, map[string]interface{}{
			"id":      agg.ID(),
			"version": version,
			"state":   agg.State(),
		})
	}
}





// Helper functions

func getIntQueryParam(r *http.Request, name string, defaultVal int) int {
	val := r.URL.Query().Get(name)
	return getInt(val, defaultVal)
}

func getInt(s string, defaultVal int) int {
	if s == "" {
		return defaultVal
	}

	intVal, err := strconv.Atoi(s)
	if err != nil {
		return defaultVal
	}

	return intVal
}


