export class InventoryGaugeElement extends PetriElement {
  static get observedAttributes() {
    return ['label', 'value', 'capacity', 'unit', 'warning-threshold', 'color']
  }

  static attributes = {
    label: { type: 'string', default: 'Resource' },
    value: { type: 'number', default: 0 },
    capacity: { type: 'number', default: 100 },
    unit: { type: 'string', default: '' },
    'warning-threshold': { type: 'number', default: 0.3 },
    color: { type: 'string', default: '' }
  }

  styles() {
    return `
      .gauge {
        padding: 1rem;
      }
      .gauge-header {
        display: flex;
        justify-content: space-between;
        margin-bottom: 0.5rem;
      }
      .gauge-label {
        font-weight: 500;
        color: #333;
      }
      .gauge-value {
        font-family: monospace;
        color: #666;
      }
      .gauge-bar {
        height: 8px;
        background: #e0e0e0;
        border-radius: 4px;
        overflow: hidden;
      }
      .gauge-fill {
        height: 100%;
        border-radius: 4px;
        transition: width 0.3s, background 0.3s;
      }
      .gauge-fill.good { background: #28a745; }
      .gauge-fill.warning { background: #ffc107; }
      .gauge-fill.danger { background: #dc3545; }
    `
  }

  template() {
    const label = this.prop('label')
    const value = this.prop('value')
    const capacity = this.prop('capacity')
    const unit = this.prop('unit')
    const warningThreshold = this.prop('warning-threshold')
    const customColor = this.prop('color')

    const percent = capacity > 0 ? (value / capacity) * 100 : 0
    const level = customColor ? ''
                : percent < warningThreshold * 100 ? 'danger'
                : percent < 50 ? 'warning'
                : 'good'
    const colorStyle = customColor ? `background: ${customColor}` : ''

    return `
      <div class="gauge">
        <div class="gauge-header">
          <span class="gauge-label">${label}</span>
          <span class="gauge-value">${value}${unit ? ` ${unit}` : ''} / ${capacity}</span>
        </div>
        <div class="gauge-bar">
          <div class="gauge-fill ${level}" style="width: ${percent}%; ${colorStyle}"></div>
        </div>
        <slot></slot>
      </div>
    `
  }
}

