// ============================================================================
// Component Registry - allows extensions to override
// ============================================================================

const registry = new Map()

export function registerComponent(tag, componentClass, { force = false } = {}) {
  const fullTag = tag.includes('-') ? tag : `${MODEL_NAME}-${tag}`

  // Check if already defined
  if (customElements.get(fullTag)) {
    if (!force) {
      console.log(`[petri] Component ${fullTag} already registered (extension override)`)
      return false
    }
  }

  // Store in registry
  registry.set(fullTag, componentClass)

  // Define custom element
  try {
    customElements.define(fullTag, componentClass)
    return true
  } catch (e) {
    console.warn(`[petri] Could not register ${fullTag}:`, e.message)
    return false
  }
}

export function getComponent(tag) {
  const fullTag = tag.includes('-') ? tag : `${MODEL_NAME}-${tag}`
  return registry.get(fullTag) || customElements.get(fullTag)
}

