export class StateDisplayElement extends PetriElement {
  static get observedAttributes() { return ['compact'] }

  styles() {
    return `
      .state-grid {
        display: grid;
        gap: 0.75rem;
      }
      .state-item {
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding: 0.5rem 0;
        border-bottom: 1px solid #f0f0f0;
      }
      .state-item:last-child {
        border-bottom: none;
      }
      .state-label {
        color: #666;
        font-size: 0.9rem;
      }
      .state-value {
        font-weight: 600;
        font-family: monospace;
      }
      .state-value.positive { color: #28a745; }
      .state-value.zero { color: #999; }
      :host([compact]) .state-item {
        padding: 0.25rem 0;
        font-size: 0.85rem;
      }
    `
  }

  template() {
    const state = this._state || {}
    const items = Object.entries(state)
      .filter(([k]) => !k.startsWith('_'))
      .map(([key, value]) => {
        const displayKey = key.replace(/_/g, ' ')
        const valueClass = value > 0 ? 'positive' : value === 0 ? 'zero' : ''
        return `
          <div class="state-item">
            <span class="state-label">${displayKey}</span>
            <span class="state-value ${valueClass}">${value}</span>
          </div>
        `
      }).join('')

    return `
      <div class="state-grid">
        <slot name="before"></slot>
        ${items}
        <slot name="after"></slot>
        <slot></slot>
      </div>
    `
  }
}

