// Navigation state
let navigationData = null
let isLoading = false

// Fetch navigation from backend
async function fetchNavigation() {
  if (isLoading) return

  isLoading = true
  try {
    const headers = {}
    const token = getAuthToken()
    if (token) {
      headers['Authorization'] = `Bearer ${token}`
    }

    const response = await fetch(`${API_BASE}/api/navigation`, { headers })
    if (response.ok) {
      navigationData = await response.json()
    } else {
      // Use fallback navigation
      navigationData = defaultNavigation
    }
  } catch (error) {
    // Use fallback navigation
    navigationData = defaultNavigation
  } finally {
    isLoading = false
  }
}

// Create navigation HTML
export async function createNavigation() {
  // Fetch navigation if not already loaded
  if (!navigationData) {
    await fetchNavigation()
  }

  const currentPath = window.location.pathname
  const user = getCurrentUser()

  // Use fetched items or defaults
  const items = navigationData?.items || defaultNavigation.items
  const brand = navigationData?.brand || defaultNavigation.brand

  const html = `
    <nav class="navigation">
      <div class="nav-brand">
        <a href="/task-manager" onclick="handleNavClick(event, '/task-manager')">
          ${brand}
        </a>
      </div>
      <ul class="nav-menu">
        ${items.map(item => {
          const isActive = currentPath === item.path ||
            (item.path !== '/' && currentPath.startsWith(item.path))
          return `
            <li class="${isActive ? 'active' : ''}">
              <a href="${item.path}" onclick="handleNavClick(event, '${item.path}')">
                ${item.icon ? `<span class="icon">${item.icon}</span>` : ''}
                ${item.label}
              </a>
            </li>
          `
        }).join('')}
      </ul>
      <div class="nav-user">
        ${user ? `
          <span class="user-name">${user.roles ? user.roles.join(', ') : user.login || user.name || 'User'}</span>
          <button onclick="handleLogout()" class="btn btn-link" style="color: rgba(255,255,255,0.8);">Logout</button>
        ` : `
          <button onclick="showLoginModal()" class="btn btn-primary btn-sm">Login</button>
        `}
      </div>
    </nav>
    ${createLoginModal()}
  `

  return html
}

