// Show login modal
window.showLoginModal = function() {
  const modal = document.getElementById('login-modal')
  if (modal) {
    modal.style.display = 'flex'
  }
}

// Hide login modal
window.hideLoginModal = function() {
  const modal = document.getElementById('login-modal')
  if (modal) {
    modal.style.display = 'none'
  }
}

// Handle role-based login
window.handleRoleLogin = async function(roles) {
  try {
    const response = await fetch(`${API_BASE}/api/debug/login`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ login: 'pilot-user', roles: roles })
    })

    if (!response.ok) {
      throw new Error('Login failed')
    }

    const data = await response.json()

    // Save auth data
    localStorage.setItem('auth', JSON.stringify(data))

    // Hide modal
    hideLoginModal()

    // Clear navigation cache to refetch with new user context
    navigationData = null

    // Dispatch auth change event
    window.dispatchEvent(new CustomEvent('auth-change'))

    // Refresh navigation
    await refreshNavigation()
  } catch (e) {
    console.error('Login error:', e)
    alert('Login failed. Please try again.')
  }
}

// Handle navigation clicks
window.handleNavClick = function(event, path) {
  event.preventDefault()
  navigate(path)
}

// Handle logout
window.handleLogout = async function() {
  try {
    const token = getAuthToken()
    if (token) {
      await fetch(`${API_BASE}/auth/logout`, {
        method: 'POST',
        headers: { 'Authorization': `Bearer ${token}` }
      })
    }
  } catch (e) {
    console.error('Logout error:', e)
  }

  // Clear local auth
  localStorage.removeItem('auth')

  // Clear navigation cache to refetch with new user context
  navigationData = null

  // Dispatch auth change event
  window.dispatchEvent(new CustomEvent('auth-change'))

  // Refresh navigation and redirect
  await refreshNavigation()
  navigate('/task-manager')
}

// Helper functions
function getCurrentUser() {
  const auth = localStorage.getItem('auth')
  if (auth) {
    try {
      return JSON.parse(auth).user
    } catch (e) {
      return null
    }
  }
  return null
}

function getAuthToken() {
  const auth = localStorage.getItem('auth')
  if (auth) {
    try {
      return JSON.parse(auth).token
    } catch (e) {
      return null
    }
  }
  return null
}

// Refresh navigation (e.g., after login/logout)
export async function refreshNavigation() {
  navigationData = null
  await fetchNavigation()
  const navElement = document.getElementById('nav')
  if (navElement) {
    navElement.innerHTML = await createNavigation()
  }
}

// Update navigation when auth changes
window.addEventListener('auth-change', async () => {
  await refreshNavigation()
})

// Update active state when route changes
window.addEventListener('route-change', () => {
  const currentPath = window.location.pathname
  document.querySelectorAll('.nav-menu li').forEach(li => {
    li.classList.remove('active')
  })

  // Find and highlight active link
  document.querySelectorAll('.nav-menu a').forEach(a => {
    const href = a.getAttribute('href')
    if (href === currentPath || (href !== '/' && currentPath.startsWith(href))) {
      a.parentElement.classList.add('active')
    }
  })
})
