// ============================================================================
// State Display Component
// ============================================================================

export class StateDisplayElement extends PetriElement {
  static get observedAttributes() { return ['compact'] }

  styles() {
    return `
      .state-grid {
        display: grid;
        gap: 0.75rem;
      }
      .state-item {
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding: 0.5rem 0;
        border-bottom: 1px solid #f0f0f0;
      }
      .state-item:last-child {
        border-bottom: none;
      }
      .state-label {
        color: #666;
        font-size: 0.9rem;
      }
      .state-value {
        font-weight: 600;
        font-family: monospace;
      }
      .state-value.positive { color: #28a745; }
      .state-value.zero { color: #999; }
      :host([compact]) .state-item {
        padding: 0.25rem 0;
        font-size: 0.85rem;
      }
    `
  }

  template() {
    const state = this._state || {}
    const items = Object.entries(state)
      .filter(([k]) => !k.startsWith('_'))
      .map(([key, value]) => {
        const displayKey = key.replace(/_/g, ' ')
        const valueClass = value > 0 ? 'positive' : value === 0 ? 'zero' : ''
        return `
          <div class="state-item">
            <span class="state-label">${displayKey}</span>
            <span class="state-value ${valueClass}">${value}</span>
          </div>
        `
      }).join('')

    return `
      <div class="state-grid">
        <slot name="before"></slot>
        ${items}
        <slot name="after"></slot>
        <slot></slot>
      </div>
    `
  }
}

// ============================================================================
// Action Button Component
// ============================================================================

export class ActionButtonElement extends PetriElement {
  static get observedAttributes() {
    return ['action', 'disabled', 'variant', 'label']
  }

  styles() {
    return `
      button {
        width: 100%;
        padding: 0.75rem 1rem;
        border: none;
        border-radius: 6px;
        cursor: pointer;
        font-size: 0.9rem;
        font-weight: 500;
        transition: all 0.2s;
        display: flex;
        align-items: center;
        justify-content: center;
        gap: 0.5rem;
      }
      button:disabled {
        opacity: 0.5;
        cursor: not-allowed;
      }
      button.primary { background: #0066cc; color: white; }
      button.primary:hover:not(:disabled) { background: #0055aa; }
      button.success { background: #28a745; color: white; }
      button.success:hover:not(:disabled) { background: #218838; }
      button.warning { background: #ffc107; color: #333; }
      button.warning:hover:not(:disabled) { background: #e0a800; }
      button.danger { background: #dc3545; color: white; }
      button.danger:hover:not(:disabled) { background: #c82333; }
      ::slotted(svg) { width: 1rem; height: 1rem; }
    `
  }

  template() {
    const action = this.getAttribute('action') || ''
    const label = this.getAttribute('label') || action.replace(/_/g, ' ')
    const variant = this.getAttribute('variant') || 'primary'
    const disabled = this.hasAttribute('disabled')

    return `
      <button class="${variant}" ${disabled ? 'disabled' : ''}>
        <slot name="icon"></slot>
        ${label}
      </button>
    `
  }

  onRender() {
    this.$('button')?.addEventListener('click', () => {
      if (!this.hasAttribute('disabled')) {
        this.emit('action', { action: this.getAttribute('action') })
      }
    })
  }
}

