// ============================================================================
// Register all default components (extensions can override by loading first)
// ============================================================================

export function registerDefaultComponents() {
  registerComponent('dashboard', DashboardElement)
  registerComponent('state-display', StateDisplayElement)
  registerComponent('action-button', ActionButtonElement)
  registerComponent('instance-card', InstanceCardElement)
  registerComponent('inventory-gauge', InventoryGaugeElement)
  registerComponent('order-flow', OrderFlowElement)
}

// Auto-register unless explicitly disabled
if (!window.__PETRI_SKIP_AUTO_REGISTER__) {
  registerDefaultComponents()
}

// Export for extensions
window.PetriElement = PetriElement
window.registerComponent = registerComponent
window.getComponent = getComponent
